import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import store from './store';
import axios from "axios";
import connect from '@/components/ui';

createApp(App).use(store).use(router).mount('#app');
axios.defaults.baseURL='htt://localhost:8080/api/';
axios.defaults.headers["cotent-type"]="application/json";
axios.defaults.headers.common.authorization = 'Bearer ${localStorage.getItem(`token`)}';
axios.defaults.headers.common['Acces-Control-Allow-Origin']='*';
const app=createApp(App);
components.forEach(connect => {
    app.component(component.name, component)
});
app.use(router).mount('#app');
